    }
  });
    
    const mailOptions = {
      //from: 'techadigun@gmail.com',
      from: '"Banka" <techadigun@gmail.com>',
      to: receiver,
      subject: subject,
      text: text
    };
    
    transporter.sendMail(mailOptions, function(error, info){
      if (error) {
        console.log(error);
      } else {
        console.log('Email sent: ' + info.response);
      }
    });
  

}

